import { makeRequest } from '../../axios.js';
import Post from '../post/Post';
import './posts.scss';
import {
  useQuery,
  useMutation,
  useQueryClient,
  QueryClient,
  QueryClientProvider,
} from '@tanstack/react-query';

const Posts = () => {
  const { isPending, error, data } = useQuery({
    queryKey: ['posts'],
    queryFn: () =>
      makeRequest.get('/posts').then(res => {
        return res.data;
      }),
  });

  // console.log(data);

  return (
    <div className="posts">
      {error
        ? 'something wrong'
        : isPending
        ? 'loading'
        : data.map(post => <Post post={post} key={post.id} />)}
    </div>
  );
};

export default Posts;
